body {
  font-family: Arial, sans-serif;
  max-width: 600px;
  margin: 20px auto;
  padding: 20px;
}
.todo-container {
  border: 1px solid #ccc;
  padding: 20px;
  border-radius: 5px;
}
input[type="text"] {
  padding: 5px;
  width: 70%;
  margin-right: 10px;
}
.add-task, .save-task, .cancel-task {
  padding: 5px 10px;
  cursor: pointer;
  margin: 0 5px;
  display: inline-block;
  background: #f0f0f0;
  border: 1px solid #ccc;
  border-radius: 3px;
}
.add-task:hover, .save-task:hover, .cancel-task:hover {
  background: #e0e0e0;
}
.trash-icon {
  font-size: 16px;
  padding: 5px;
  width: 30px;
  height: 30px;
  line-height: 20px;
  text-align: center;
  background: #f8f8f8;
  border: 1px solid #ccc;
  border-radius: 3px;
  cursor: pointer;
  display: inline-block;
}
.trash-icon:hover {
  background: #ff4d4d;
  color: white;
  border-color: #ff4d4d;
}
li {
  list-style: none;
  margin: 10px 0;
}
.filter-controls {
  margin: 10px 0;
}
.filter-all {
  padding: 5px 10px;
  cursor: pointer;
  margin: 0 5px;
  display: inline-block;
  background: #f0f0f0;
  border: 1px solid #ccc;
  border-radius: 3px;
}
.filter-all:hover {
  background: #e0e0e0;
}
.filter-all.active {
  background: #007bff;
  color: white;
  border-color: #007bff;
}
